(function($){$(document).ready(function(){if(location.href.indexOf("#sy-")!==-1){var scrollPos=location.href.split("#sy-").pop();$(window).scrollTop(scrollPos)}});$.fn.antiScroll=function(){return this.each(function(){$(this).click(function(){if($(this).attr("href").indexOf("#")!==-1&&$(this).attr("href").indexOf("#sy-")===-1){return }var currentHref=$(this).attr("href");if(currentHref.indexOf("#sy-")!==-1){currentHref=currentHref.replace(/#sy-[0-9]*/g,"")}$(this).attr("href",currentHref+"#sy-"+$(window).scrollTop())})})}}(jQuery));